import React, { useState, useCallback } from "react";
import { BrowserView } from "react-device-detect";
import { hashDivrap } from "./styledComponent";
const ShowSearch = () => {
  const [hashtag, setHashtag] = useState("");
  const [hashArr, setHashArr] = useState([]);
  const [color, setColor] = useState("blue");

  const onChangeHashtag = (e) => {
    setHashtag(e);
  };

  const pushTag = (e) => {
    const { name } = e.target;
    setHashtag(e.target.name);
    setHashArr((hashArr) => [...hashArr, hashtag]);
    color === "blue" ? setColor("yellow") : setColor("blue");
  };
  return (
    <>
      <section className="Search">
        <div className="Search-tag">
          <button
            className="tag-button"
            name="cute"
            color={color}
            onClick={pushTag}
          >
            cute
          </button>
          <span
            className="tag-button"
            name="lovely"
            color={color}
            onClick={pushTag}
          >
            lovely
          </span>
          <button
            className="tag-button"
            name="basic"
            color={color}
            onClick={pushTag}
          >
            basic
          </button>
          <button
            className="tag-button"
            name="abcdse"
            color={color}
            onClick={pushTag}
          >
            abcdse
          </button>
        </div>
        <div>{hashArr}</div>
        <div className="Search-color">Color</div>
      </section>
    </>
  );
};

export default ShowSearch;
