import React, { useState, useCallback } from "react";
import { BrowserView } from "react-device-detect";
import { hashDivrap } from "./styledComponent";
const ShowSearch = () => {
  const [hashtag, setHashtag] = useState("");
  const [hashArr, setHashArr] = useState([]);
  const [addMode, setaddMode] = useState(false);
  const onChangeHashtag = (e) => {
    setHashtag(e);
  };

  const pushTag = (e) => {
    const { name } = e.target;
    setHashtag(e.target.name);
    setHashArr((hashArr) => [...hashArr, hashtag]);
  };
  return (
    <>
      <section className="Search">
        <div className="Search-tag">
          <button
            className="Search-button"
            name="cute"
            onChange={onChangeHashtag}
            onClick={pushTag}
          >
            cute
          </button>
          <button className="Search-button" name="lovely" onClick={pushTag}>
            lovely
          </button>
          <button className="Search-button" name="basic" onClick={pushTag}>
            basic
          </button>
          <button className="Search-button" name="abcdse" onClick={pushTag}>
            abcdse
          </button>
        </div>
        <div>{hashArr}</div>
        <div className="Search-color">Color</div>
      </section>
    </>
  );
};

export default ShowSearch;
