import React, { useState, useCallback } from "react";
import { BrowserView } from "react-device-detect";
import {
  hashDivrap,
  PostSection,
  PostTitleDiv,
  PostTitle,
  PostListDiv,
  LoadingDiv,
  LoadingImg,
  CursorDiv,
} from "./styledComponent";
import { useNavigate } from "react-router-dom";
import {
  faArrowsRotate,
  faPenToSquare,
} from "@fortawesome/free-solid-svg-icons";

import loadingIcon from "./loading2.svg";

import EachPost from "./EachPost";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const ShowSearch = () => {
  const [hashtag, setHashtag] = useState("");
  const [hashArr, setHashArr] = useState([]);
  const [select, setSelect] = useState("false");

  const onChangeHashtag = (e) => {
    setHashtag(e);
  };

  const pushTag = (e) => {
    const { name } = e.target;
    setHashtag(e.target.name);
    setHashArr((hashArr) => [...hashArr, hashtag]);
  };

  //const deleteTag = (e) => {};

  const deleteAll = () => {
    setHashArr((hashArr) => []);
  };
  return (
    <>
      <PostSection>
        <PostTitleDiv>
          <PostTitle>My Style</PostTitle>
        </PostTitleDiv>
        <PostListDiv></PostListDiv>
      </PostSection>
      <section className="Search">
        <div className="Search-tag">
          <button className="tag-button" name="cute" onClick={pushTag}>
            cute
          </button>
          <button className="tag-button" name="lovely" onClick={pushTag}>
            lovely
          </button>
          <button className="tag-button" name="basic" onClick={pushTag}>
            basic
          </button>
          <button className="tag-button" name="abcdse" onClick={pushTag}>
            abcdse
          </button>
          <button onClick={deleteAll}>초기화</button>
        </div>
        <div>
          {hashArr.map((name) => (
            <button name={name}>{name}</button>
          ))}
        </div>

        <div className="Search-color">Color</div>
      </section>
    </>
  );
};

export default ShowSearch;
