import React, { useState, useCallback, useEffect } from "react";
import {
  PostSection,
  PostTitleDiv,
  PostTitle,
  PostListDiv,
} from "./styledComponent";

import { axios } from "axios";

const ShowSearch = () => {
  const [hashtag, setHashtag] = useState("");
  const [hashArr, setHashArr] = useState([]);
  const [select, setSelect] = useState("false");

  //   useEffect(() => {
  //     axios.get("/").then((response) => {
  //       console.log(response);
  //     });
  //   }, []);

  const onChangeHashtag = (e) => {
    setHashtag(e);
  };

  const pushTag = (e) => {
    const { name } = e.target;
    setHashtag(e.target.name);
    setHashArr((hashArr) => [...hashArr, hashtag]);
  };

  //const deleteTag = (e) => {};

  const deleteAll = () => {
    setHashArr((hashArr) => []);
  };
  return (
    <>
      <PostSection>
        <PostTitleDiv className="TitleDiv">
          <PostTitle>Select My Style</PostTitle>
        </PostTitleDiv>
        <PostListDiv></PostListDiv>
      </PostSection>
      <div className="selectStyle">
        <div className="selectStyle-menu">
          <div className="selectStyle-menu__title">Select TPO</div>
          <section className="selectStyle-menu__content">
            <button
              className="tag-button tag-button__clear"
              onClick={deleteAll}
            >
              Tag Clear
            </button>
            <div className="Search-tag">
              <button
                className="tag-button btn first"
                name="cute"
                onClick={pushTag}
              >
                cute
              </button>
              <button className="tag-button" name="lovely" onClick={pushTag}>
                lovely
              </button>
              <button className="tag-button" name="basic" onClick={pushTag}>
                basic
              </button>
              <button className="tag-button" name="abcdse" onClick={pushTag}>
                abcdse
              </button>
            </div>
            <div>
              {hashArr.map((name) => (
                <button name={name}>{name}</button>
              ))}
            </div>
          </section>
        </div>
        <div className="selectStyle-menu">
          <div className="selectStyle-menu__title">Select Color</div>
          <section className="selectStyle-menu__content">
            <button
              className="tag-button tag-button__clear"
              onClick={deleteAll}
            >
              Tag Clear
            </button>
            <div className="Search-tag">
              <button className="tag-button" name="cute" onClick={pushTag}>
                cute
              </button>
              <button className="tag-button" name="lovely" onClick={pushTag}>
                lovely
              </button>
              <button className="tag-button" name="basic" onClick={pushTag}>
                basic
              </button>
              <button className="tag-button" name="abcdse" onClick={pushTag}>
                abcdse
              </button>
            </div>
            <div>
              {hashArr.map((name) => (
                <button name={name}>{name}</button>
              ))}
            </div>
          </section>
        </div>
      </div>
    </>
  );
};

export default ShowSearch;
