import React, { useState, useRef, useEffect } from "react";
import {
  ContentsInput,
  TitleInput,
  PostSection,
  PostWriteDiv,
  PostSubmit,
} from "./styledComponent";
import WriteTitle from "./WriteTitle";
function InputPost({ onChange, contents, title }) {
  const titleInput = useRef();
  const contentsInput = useRef();
  useEffect(() => {
    titleInput.current.focus();
  }, []);

  const onKeyUp = (e) => {
    if (e.key === "Enter") {
      contentsInput.current.focus();
    }
  };

  return (
    <PostSection>
      <WriteTitle />
      <PostWriteDiv>
        <TitleInput
          name="title"
          type="text"
          value={title}
          placeholder="제목을 입력해주세요. (15자 이내)"
          onChange={onChange}
        />
        <ContentsInput
          name="contents"
          value={contents}
          cols="30"
          rows="10"
          onChange={onChange}
        ></ContentsInput>
      </PostWriteDiv>
      <PostSubmitDiv>
        <PostSubmit>작성완료</PostSubmit>
      </PostSubmitDiv>
    </PostSection>
  );
}

export default InputPost;
