import React, { useState, useEffect } from "react";
import {
  PostSection,
  PostTitleDiv,
  PostTitle,
  PostListDiv,
  CursorDiv,
} from "./styledComponent";
import { useNavigate } from "react-router-dom";
import {
  faArrowsRotate,
  faPenToSquare,
} from "@fortawesome/free-solid-svg-icons";

import loadingIcon from "./loading2.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const initialPostList = [
  { id: 1, title: "오늘 뭐 입지?" },
  { id: 2, title: "오늘 뭐 날씨는?" },
];

function ShowMyPage() {
  const [loading, setLoading] = useState(true);
  const [isPost, setIsPost] = useState(false);
  const [postList, setPostList] = useState([]);

  const addPost = () => {
    setPostList((postList) => [...postList, { id: 4, title: "오잉오잉" }]);
  };

  const navigate = useNavigate();
  const goWrite = () => {
    navigate("./write");
  };

  useEffect(() => {
    setTimeout(() => {
      setPostList(initialPostList);
      setLoading(false);
    }, 600);
  }, []); // 렌더링되고 한번만 실행 원한다.
  return (
    <PostSection>
      <PostTitleDiv>
        <CursorDiv>
          <FontAwesomeIcon onClick={addPost} icon={faArrowsRotate} />
        </CursorDiv>
        <PostTitle>My Page</PostTitle>
        <CursorDiv>
          <FontAwesomeIcon onClick={goWrite} icon={faPenToSquare} />
        </CursorDiv>
      </PostTitleDiv>
      <PostListDiv></PostListDiv>
    </PostSection>
  );
}

export default ShowMyPage;
